mapper.select:
  	select * from as_accountdetail where userId = #{id}

  		and detailType = #{detailType}


		and detailDateTime >= #{start}


		and detailDateTime <= #{end}

	order by id
	LIMIT #{lineNum},#{pageSize}

	select count(0) from  as_accountdetail where userId = #{id}

  		and detailType = #{detailType}


		and detailDateTime >= #{start}


		and detailDateTime <= #{end}

select a.id, a.money, a.accountMoney, a.memo, a.detailDateTime, u.id as userid, u.userName from as_accountdetail a inner join as_user u on a.userId = u.id where a.detailType = 4
and a.detailDateTime >= #{start}
and a.detailDateTime <= #{end}
order by a.id

select a.id, a.money, a.accountMoney, a.memo, a.detailDateTime, u.id as userid, u.userName from as_accountdetail a inner join as_user u on a.userId = u.id where 1 = 1
and a.detailDateTime >= #{start}
and a.detailDateTime <= #{end}
order by a.id
